def scrape_page(url):
    response = requests.get(url)
    response.raise_for_status()
    soup = BeautifulSoup(response.text, 'html.parser')
    name = soup.find('h1').text
    description_tag = soup.find('p')  # Assuming the description is in the first 'p' tag
    description = description_tag.get_text(strip=True) if description_tag else None

    if description:
        description = description.encode('utf-8').decode('unicode_escape')  # Decoding Unicode escape sequences
        description = description.replace('\n', ' ') 
    address_tag = soup.find('p', style="margin-top: 0;")
    address = address_tag.get_text(strip=True).replace('Address:', '').strip() if address_tag else None
    if address:
            address = address.replace('\u00c2', ' ')
            address = address.replace('\u00a0', ' ')
            address = address.replace('map', '').strip() 
    phone = None
    email = None

    # Finding phone number and email by iterating through 'p' tags
    for p_tag in soup.find_all('p'):
        if 'Phone:' in p_tag.text:
            phone = p_tag.text.replace('Phone:', '').strip()
        elif 'Email:' in p_tag.text:
            email = p_tag.find('a')['href'].replace('mailto:', '').strip()
    website_tag = soup.find('a', href=True, target="_blank")
    website = website_tag['href'].strip() if website_tag else None
    discount_info_tag = soup.find('div', style="background: #eee7f1; border: 1px solid black; padding: 5px 10px;")
    discount_info = discount_info_tag.get_text(strip=True) if discount_info_tag else None

    data = {
        'name': name,
        'description': description,
        'address': address,
        'phone': phone,
        'email': email,
        'website': website,
        'discount_info': discount_info
    }
    return data



def save_to_json(data):
    with open('data.json', 'w') as f:
        json.dump(data, f, indent=2)

if __name__ == "__main__":
    soup = BeautifulSoup(html_content, 'html.parser')
    base_url = 'https://www.northwestern.edu'
    links = [base_url + a['href'] for a in soup.find_all('a', href=True)]

    data_list = []  

    for link in links:
        data = scrape_page(link)
        if data:  
            data_list.append(data)

    save_to_json(data_list) 

    


